import React from "react";
import Login from "./components/login/login";
import Register from "./components/register/register";
import PasswdReset from "./components/passwd_reset/passwd_reset";

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      currentRoute: "/login",
      routes: {
        '/passwd_reset': PasswdReset,
        '/login': Login,
        '/register': Register
      }
    };
  }

  render() {
    return (
      <div className="App">
        <PasswdReset />
      </div>
    );
  }
}

export default App;
