import React from "react";

class Test extends React.Component {
  render() {
    return <h1>Hello</h1>
  }
}



class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      auth: 0,
      currentComponent: null,
      components : [
        <Login />,
        <Register />,
        <PasswordReset />
      ]
    };
  }

  render() {
    return (
      <div className="App">
        {this.state.currentComponent}
      </div>
    )
  }

  componentDidMount() {
    if (this.state.auth === 0) {
      this.setState({ currentComponent: <Login /> })
    }
  }
}

class Header extends React.Component {
  render() {
    return (
      <div id="header" style={{ border: "1px solid black" }}>
        <nav>
          <h4 id="app_title">SOCIAL APP</h4>
          <form>
            <input type="text" />
            <button type="submit">\</button>
          </form>
          <ul>
            <li><button>HOME</button></li>
            <li><button>profile</button></li>
            <li><button>logout</button></li>
          </ul>
        </nav>
      </div>
    );
  }
}

class Register extends React.Component {

  handleClick = () => {
    this.props.setCurrentComponent(<Login />);
  }

  render() {
    return (
      <div>
        <h4>REGISTER</h4>
        <form>
          <input type="text" placeholder=" username" />
          <input type="password" placeholder=" password" />
          <input type="text" placeholder=" re-type password" />
          <input type="submit" value="register" />
        </form>
        <span><a href="login" onClick={this.handleClick}>click here</a> to login</span>
      </div>
    );
  }
}

class Login extends React.Component {
  render() {
    return (
      <div>
        <h4>LOGIN</h4>
        <form>
          <input type="text" placeholder=" username" />
          <input type="password" placeholder=" password" />
          <input type="submit" value="login" />
        </form>
        <span>forgot password <a href="password_reset">click here</a></span>
        <span>new user? <a href="register">click here</a></span>
      </div>
    );
  }
}

class PasswordReset extends React.Component {
  render() {
    return (
      <div>
        <h4>Change password</h4>
        <form>
          <input type="password" placeholder=" enter new password" />
          <input type="text" placeholder=" re-enter password" />
          <input type="submit" value="change" />
        </form>
      </div>
    );
  }
}

export default App;
