import React from "react";
import Login from "./components/login/login";
import Register from "./components/register/register";
import PasswdReset from "./components/passwd_reset/passwd_reset";

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      currentRoute: "",
      routes: {
        '/passwd_reset': <PasswdReset />,
        '/login': <Login />,
        '/register': <Register />
      }
    };
  }

  render() {
    return (
      <div className="App">
        {this.state.routes[this.state.currentRoute]}
      </div>
    );
  }

  componentDidMount() {
    let route = '/' + window.location.href.split('/').pop();
    if (route !== this.state.currentRoute) {
      this.setState({ currentRoute: route });
    }
  }
}

export default App;
