import React from "react";

class Test extends React.Component {
  render() {
    return <h1>Hello</h1>
  }
}



class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      auth: 0,
      currentComponent: null,
      components: {
        'Login': <Login setCurrentComponent={this.setCurrentComponent} />,
        'Register': <Register setCurrentComponent={this.setCurrentComponent} />,
        'PasswordReset': <PasswordReset setCurrentComponent={this.setCurrentComponent} />
      }
    };
  }

  setCurrentComponent = (newComponent) => {
    this.setState({ currentComponent: this.state.components[newComponent] });
  }

  render() {
    return (
      <div className="App">
        {this.state.currentComponent}
      </div>
    )
  }

  componentDidMount() {
    if (this.state.auth === 0) {
      this.setState({ currentComponent: <Register setCurrentComponent={this.setCurrentComponent} /> })
    }
  }
}

class Header extends React.Component {
  render() {
    return (
      <div id="header" style={{ border: "1px solid black" }}>
        <nav>
          <h4 id="app_title">SOCIAL APP</h4>
          <form>
            <input type="text" />
            <button type="submit">\</button>
          </form>
          <ul>
            <li><button>HOME</button></li>
            <li><button>profile</button></li>
            <li><button>logout</button></li>
          </ul>
        </nav>
      </div>
    );
  }
}

class Register extends React.Component {

  handleClick = () => {
    this.props.setCurrentComponent('Login');
  }

  render() {
    return (
      <div>
        <h4>REGISTER</h4>
        <form>
          <input type="text" placeholder=" username" />
          <input type="password" placeholder=" password" />
          <input type="text" placeholder=" re-type password" />
          <input type="submit" value="register" />
        </form>
        <p><span onClick={this.handleClick}>click</span> to login</p>
      </div>
    );
  }
}

class Login extends React.Component {
  handleClickReg = () => {
    this.props.setCurrentComponent('Register');
  }

  handleClickPwdReset = () => {
    this.props.setCurrentComponent('PasswordReset');
  }

  render() {
    return (
      <div>
        <h4>LOGIN</h4>
        <form>
          <input type="text" placeholder=" username" />
          <input type="password" placeholder=" password" />
          <input type="submit" value="login" />
        </form>
        <span>forgot password <button onClick={this.handleClickPwdReset}>click here</button></span>
        <span>new user? <button onClick={this.handleClickReg}>click here</button></span>
      </div>
    );
  }
}

class PasswordReset extends React.Component {
  render() {
    return (
      <div>
        <h4>Change password</h4>
        <form>
          <input type="password" placeholder=" enter new password" />
          <input type="text" placeholder=" re-enter password" />
          <input type="submit" value="change" />
        </form>
        <span><button>Click</button> to login</span>
      </div>
    );
  }
}



export default App;
